package com;

public class MetodosString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//Metodos o funcionalidades de la clase String
		
 String fecha = "Hoy es 7-Febrero-2024";//Declaramos la variable
 
//*METODO .length() = devuelve el tamaño en valor entero de nuestra cadena de texto
 System.out.println(fecha.length());
 System.out.println(fecha.toUpperCase());
 System.out.println(fecha.replace("H","Q"));
 System.out.println(fecha.equals("hoy es 7 de febrero del 2024"));//False
 
 
 
	}

}
